class Solution {
public:
    vector<int> distinctDifferenceArray(vector<int>& nums) {
        unordered_set<int> st;
        int n = nums.size();
        vector<int> left(n, 0);

        for (int i = 0; i < n; ++i)
        {
            st.insert(nums[i]);
            left[i] = st.size();
        }

        st.clear();

        for (int i = n - 2; i >= 0; --i)
        {
            st.insert(nums[i + 1]);
            left[i] = left[i] - st.size();
        }

        return left;
    }
};
