func distinctDifferenceArray(nums []int) []int {

    answerArray := make([]int,len(nums))
    start := 0

    for i:=0 ; i<len(nums) ; i++ {

        diff := 0

        //prefix map creation
        prefixMap := make(map[int]bool)
        for j:=start ; j<= i ; j++ {
            if _ , ok := prefixMap[nums[j]] ; !ok{
                prefixMap[nums[j]] = true
            }
        }

        //suffix map creation
        suffixMap := make(map[int]bool)
        if i != len(nums)-1 {
            for k:=i+1 ; k<len(nums) ; k++ {
                if _ , ok := suffixMap[nums[k]] ; !ok{
                    suffixMap[nums[k]] = true
                }
            }
            diff = len(prefixMap) - len(suffixMap)
        }else{
            diff = len(prefixMap) - len(suffixMap)
        }
        //append the difference into the answer array
        answerArray[i] = diff
    }
    return answerArray

}

