class Solution {

    /**
     * @param Integer[] $nums
     * @return Integer[]
     */
 function distinctDifferenceArray($nums) {
        $res = [];
        for($i=0; $i<count($nums); $i++){
            $suffix = $this->countSuffix($i, $nums);
            $prefix = $this->countPrefix($i, $nums);
            $res[] = $prefix - $suffix;
        }

        return $res;
    }

    function countSuffix($tar, $array){
      $res = [];
        for($i = $tar+1; $i<count($array); $i++){
          $el = $array[$i];
          if(!in_array($el, $res)){
            $res[] = $el;
          }
        }
        return count($res);
    }

       function countPrefix($tar, $array){
      $res = [];
        for($i = 0; $i<=$tar; $i++){
          $el = $array[$i];
