use std::collections::HashSet;

impl Solution {
    pub fn distinct_difference_array(nums: Vec<i32>) -> Vec<i32> {
        (0..nums.len())
            .map(|index| {
                let prefix = nums
                    .iter()
                    .enumerate()
                    .filter(|(i, _v)| *i <= index)
                    .map(|(_, &v)| v)
                    .collect::<HashSet<i32>>()
                    .len() as i32;
                let suffix = nums
                    .iter()
                    .skip(index + 1)
                    .map(|&v| v)
                    .collect::<HashSet<i32>>()
                    .len() as i32;
                prefix - suffix
            })
            .collect::<Vec<i32>>()
    }
}
```

