class Solution {
public:
	vector<int> distinctDifferenceArray(vector<int>& nums) {

		int inputSize = nums.size();

		if(inputSize == 1){
			return {1};
		}

		bool hash[51] = {false};

		vector<int> ans(inputSize,0);

		ans[0] = 1;
		hash[nums[0]] = true;


		for (int i = 1; i < inputSize; ++i)
		{
			if(hash[nums[i]]){
				ans[i] = ans[i-1];
			}
			else{
				ans[i] = ans[i-1] + 1;
				hash[nums[i]] = true;
			}	
		} 

		int loopVar = 1;

		for (int i = 0; i < 51; ++i)
		{
			hash[i] = false;
		}

		hash[nums[inputSize-1]] = true;

		for (int i = inputSize-2; i >= 0; --i)
		{
			ans[i] -= loopVar;
			if(!hash[nums[i]]){
				loopVar++;
				hash[nums[i]] = true;
			}	
		} 

		return ans;

	}
};

```

