var distinctDifferenceArray = function(nums) {
    const res = [];
    for(let i=0;i<nums.length;i++) {
        const lSet = new Set(); // count of distinct left items
        const rSet = new Set(); // count of distinct right items

        for(let j=i;j>-1;j--) {
            lSet.add(nums[j]); // counting left items
        }

        for(let k=i+1;k<nums.length;k++) {
            rSet.add(nums[k]); // counting right items
        }

        res[i] = lSet.size - rSet.size; // calculating difference
    }

    return res;
};
