var distinctDifferenceArray = function(nums) {
    let result = []
    for (let i = 0; i < nums.length; i++) {
      const leading = nums.slice(0, i + 1)
      const trailing = nums.slice(i + 1)
      result.push(new Set(leading).size - new Set(trailing).size)
    }
