            // removing current element from suffix
            if(map.get(nums[i]) == 1){
                map.remove(nums[i]);
            }else{
                map.put(nums[i], map.get(nums[i]) - 1);
            }
            // take difference
            ans[i] = prefix.size()-map.size();
        }
