            val*=(2LL);
        }
        return val;
    }

    long long rec(vector<int> &arr,int id,int k){
        if(id>=arr.size())
            return 0;
        if(dp[id][k]!=-1)
            return dp[id][k];
        long long ans = 0;
        /// how many times should I multiply arr[id] with 2....
        for(int i = 0;i<=k;i++){
            ans = max(ans,(cal(i)*arr[id])|(rec(arr,id+1,k-i)));
        }

        return dp[id][k] = ans;

