class Solution {
public:
    long long maximumOr(vector<int>& nums, int k) {
        int n = nums.size(),i;
        if(n==1)return ((nums[0]*1LL)<<k);
        vector<int> p(n,0),s(n,0);
        p[0] = nums[0];
        for(i = 1; i < n; i++){
            p[i] = (p[i-1]|nums[i]);
        }
        s[n-1] = nums[n-1];
        for(i = n-2; i >=0; i--){
            s[i] = (s[i+1]|nums[i]);
        }
        long long int ans = 0;
        for(int i = 0; i < n; i++){
            if(i==0){
                ans = max(ans,((nums[i]*1LL)<<k)|s[i+1]);
            }else if(i==n-1){
                ans = max(ans,((nums[i]*1LL)<<k)|p[i-1]);
            }else{
                ans = max(ans,((nums[i]*1LL)<<k)|s[i+1]|p[i-1]);
            }
        }
        return ans;
    }
};
