#define ll long long
class Solution {
public:
    long long maximumOr(vector<int>& arr, int k){
        int n = arr.size();
        ll dp[n + 1][k + 1];

        for(int i = n; i >= 0; i--){
            for(int j = 0; j < k + 1; j++){
                if(i == n){
                    dp[i][j] = 0;
                    continue;
                }

                ll maxi = arr[i] | dp[i + 1][j];

                for(ll l = 1; l <= j; l++){
                    ll value = arr[i];        
                    ll cur = (value << l);

                    maxi = max(maxi, cur | dp[i + 1][j - l]);
                }
                dp[i][j] = maxi;
            }
        }
        return dp[0][k];
    }
};
```
