class Solution {
    public long maximumOr(int[] nums, int k) {
        long[][] memo = new long[nums.length][k + 1];
        for (int i = 0; i < nums.length; i++) {
            Arrays.fill(memo[i], -1);
        }
        return rec(nums, 0, k, memo);
    }

    private long rec(int[] nums, int i, int k, long[][] memo) {
        if (i == nums.length) {
            return 0;
        }
        if (memo[i][k] != -1) {
            return memo[i][k];
