        long maxi = nums[i] | rec(nums, i + 1, k, memo);

        for (int j = 1; j <= k; j++) {
            long value = nums[i];
            long cur = (value << j);
            maxi = Math.max(maxi, cur | rec(nums, i + 1, k - j, memo));
        }
        memo[i][k] = maxi;
        return maxi;
    }
}
