class Solution:
    def maximumOr(self, nums: List[int], k: int) -> int:
        n = len(nums)

        right = [0] * n
        for i in reversed(range(n - 1)):
            right[i] = right[i + 1] | nums[i + 1]

        left = [0] * n
        for i in range(1, n):
            left[i] = left[i - 1] | nums[i - 1]

        return max(
            l | (num << k) | r
            for l, num, r in zip(left, nums, right)
        )
