class Solution {
public:
    #define ll long long
    ll memo[100001][16];
   long long rec(int i,int j,vector<int>&nums,int k)
   {
       if(i>=nums.size()) return 0;
       if(memo[i][j] !=- 1) return memo[i][j];

       long long with=0;
       long long without=0;
       without=nums[i] or rec(i+1,j,nums,k) ;
       for(int a=1;a<=j;a++)
       {
           if(j-a>=0)
           {
               long long temp = rec(i+1,j-a,nums,k) ;
               ll cur=(ll)nums[i]*pow(2,a) ;
               temp =temp or cur;
               // cout<<i<<" : "<<a<<" "<<temp<<endl;
               with=max(temp,with);
           }   
       }

       // cout<<i<<" : "<<j<<" :: "<<with<<" "<<without<<endl;
       return memo[i][j]=max(with,without);
   }
   long long maximumOr(vector<int>& nums, int k) {
       memset(memo, -1, sizeof(memo));
       return rec(0,k,nums,k);
   }
};
