var maximumOr = function(nums, k) {
   let n = nums.length;
    let prev = [0n];
    let post = [0n];
    for (let i = 0; i < n; i++) prev.push(prev[prev.length - 1] | BigInt(nums[i]))
    for (let i = n - 1; i >= 0; i--) post.push(post[post.length - 1] | BigInt(nums[i]));
    let res = 0n;
    for (let i = 0; i < n; i++) {
        let cur = BigInt(nums[i]);
        let v = cur * BigInt(2 ** k) | prev[i] | post[n - i - 1];
        if (v > res) {
            res = v;
        }
    }
    return Number(res);
};
