class Solution {
    public long maximumOr(int[] nums, int k) {
        int len = nums.length;
        long pre[] = new long[len + 1];
        long suf[] = new long[len + 1];
        long res = 0, pow = 1;
        for(int i = 0; i < k; i++) {
            pow *= 2;
