            Sn = 0
            for i in range(45):
                if B[i]>1: Sn+=(1<<i)
                elif B[i]==1 and (1<<i)&n==0: Sn+=(1<<i)
                elif (n<<k)&(1<<i): Sn+=(1<<i)

