class Solution {
public:
    long long maximumOr(vector<int>& nums, int k) {
        int n = nums.size();

        vector<long long> prefixOr(n, 0), suffixOr(n, 0);

        for (int i = 1; i < n; i++) {
            prefixOr[i] = (prefixOr[i-1]|nums[i-1]);
        } 

        for (int i = n-2; i >= 0; i--) {
            suffixOr[i] = (suffixOr[i+1]|nums[i+1]);
        }

        long long p = pow(2,k);
        long long ans = 0;
        for (int i = 0; i < n; i++) {
            ans = max(ans, (prefixOr[i]|suffixOr[i])|(nums[i]*p));
        }

        return ans;
    }
};
```

