class Solution {
public:
    long long maximumOr(vector<int>& nums, int k) {
        long long ans = 0;
        long long aur = 0;
        vector<int> setbit(34, 0);
        for (int j = 0; j < nums.size(); j++){
            aur |= nums[j];
            int count = 0;
            int temp = nums[j];
            while(temp){
                if(temp % 2){
                    setbit[count]++;
                }
                temp /= 2;
                count++;
            }
        }
        for (int i = 0; i < nums.size(); i++){
            long long orr = aur;
            long long x = nums[i] * pow(2, k);
            int count = 0;
            int temp = nums[i];
            while(temp){
                if(temp % 2){
                    if(setbit[count] == 1){
                        orr ^= (long long)pow(2, count);
                    }
                }
                temp /= 2;
                count++;
            }
            orr |= x;
            ans = max(ans, orr);
        }
        return ans;
    }
};
