impl Solution {
    pub fn maximum_or(nums: Vec<i32>, k: i32) -> i64 {
        let mut count= vec![0; 32];

        for a in &nums {
            let (mut a, mut i) = (*a, 0);
            while a > 0 {
                count[i] += a % 2;
                a /= 2;
                i += 1;
            }
        }

        let mut ret = 0;

        for a in nums {
            let mut t = 0;
            for i in 0 .. 32 {
                if count[i] == 0 { continue }
                if count[i] == 1 && a & (1 << i as i64) > 0 { continue }
                t |= 1 << (i as i64);
            }
            t |= ((a as i64) << (k as i64));   
            ret = ret.max(t);
        }

        ret
    }
}
```

