class Solution:
    def maximumOr(self, nums: List[int], k: int) -> int:
        if len(nums)==1:
            return nums[0]*(2**k)
        sufix = []
        prefix = []
        sufix.append(nums[0])
        prefix.append(nums[-1])

        for i in range(1,len(nums)):
            sufix.append(sufix[-1]|nums[i])

        for i in range(len(nums)-2,-1,-1):
            prefix.append(prefix[-1]|nums[i])
        prefix = prefix[::-1]

        n = 2**k
        res=[]
        for i in range(len(nums)):
            if i == 0:
                res.append(nums[i]*n|prefix[i+1])
            elif i == len(nums)-1:
                res.append(nums[i]*n|sufix[-2])
            else:
                res.append(nums[i]*n|sufix[i-1]|prefix[i+1])
        return max(res)
