class Solution {
public:
    long long maximumOr(vector<int>& nums, int k) {
        int p1 = 0, p2 = 0;
        for(const auto num : nums) {
            p2 |= p1 & num;
            p1 |= num;
        }
        long long maxn = 0;
        for(const auto num : nums) {
            long long t = num;
            t = (t << k) | p1 - num | p2;
            if(maxn < t) {
                maxn = t;
            }
        }
        return maxn;
    }
};
```

