class Solution {
public:
    void help(vector<int>&set,long long i){
        //to make record of set bits of i
        int j=0;
        while(i){
            if(i&1){
                set[j]++;
            }
            i/=2;
            j++;            
        }
        return ;
    }
    void reduce(vector<int>&set,long long temp){
        //to remove the record of set bits of i
        int j=0;
        while(temp){
            if(temp&1){
                set[j]--;
            }
            temp/=2;
            j++;
        }
        return ;
    }
    long long maximumOr(vector<int>& nums, int k) {
        vector<int> set(64,0);
        int n=nums.size();

        for(int i=0;i<n;i++)           help(set,nums[i]);
        //to store record of all the numbers

        long long ans=0;

        for(int i=0;i<n;i++){

            reduce(set,nums[i]);    //remove the current record of nums[i]  and instead add record of nums[i]*(2^k)


            long long temp=nums[i]*pow(2,k);
            help(set,temp);

            //now evaluating the result of or operation 
            long long cur=0;
            for(int i=0;i<64;i++){
                if(set[i]>0)    cur+=pow(2,i);
            }

            //undo changes in set vector to proceed in next iteration
            reduce(set,temp);
            help(set,nums[i]);
            //store the answer
            ans=max(ans,cur);
        }
        return ans;
    }
};
```

