class Solution {
    public long maximumOr(int[] nums, int k) {

        long ans=0;
        int n=nums.length;

        long[] pre=new long[n+1];
        long[] suff=new long[n+1];

        Arrays.fill(pre,0); //prefix array of OR operation
        Arrays.fill(suff,0); //suffix array of OR operation

        long p=1;
        p=p<<k;     //power of 2 after left shifting by k

        for(int i=0;i<n;i++){
            pre[i+1]=pre[i]|nums[i];
        }
        for(int i=n-1;i>=0;i--){
            suff[i]=suff[i+1]|nums[i];
        }

        for(int i=0;i<n;i++){
            ans=Math.max(ans,pre[i]|(p*nums[i])|suff[i+1]);
        }

        return ans;
    }
}
