        std::vector<int> suffix(nums.size() + 1, 0);
        for (int i = nums.size() - 2; i >= 0; i--) {
            suffix[i] = suffix[i + 1] | nums[i + 1];
        }
        long res = 0;
        int prefix = 0;
        int kp = 1 << k;
        for(int i = 0; i < nums.size(); i++) {
            res = std::max(res, (prefix | (long) nums[i] * kp) | suffix[i]);
            prefix = prefix | nums[i];
        }
        return res;
    }
};
