class Solution {
    fun maximumOr(A: IntArray, K: Int): Long {
        var N = A.size
        var (L, R) = Pair(A.copyOf(), A.copyOf())
        for (i in 1 until N) {
            var j = N - 1 - i
            L[i] = L[i] or L[i - 1]
            R[j] = R[j] or R[j + 1]
