        var best = 0L
        for (i in 0 until N) {
            var cand = (if (0 <= i - 1) L[i - 1] else 0).toLong() or (A[i].toLong() shl K) or (if (i + 1 < N) R[i + 1] else 0).toLong()
            best = Math.max(best, cand)
        }
        return best
    }
