class Solution {
public:
    long long maximumOr(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        vector<long long> pre(n + 1, 0);  // Stores prefix OR values
        vector<long long> suf(n + 1, 0);  // Stores suffix OR values

        // Calculate prefix OR values
        for (int i = 0; i < n; i++) {
            pre[i + 1] = pre[i] | nums[i];
        }

        // Calculate suffix OR values
        for (int i = n - 1; i >= 0; i--) {
            suf[i] = suf[i + 1] | nums[i];
        }

        // Iterate through each element of the array
        for (int i = 0; i < n; i++) {
            long long num = nums[i];

            // Apply the operation on the element k times
            for (int j = 0; j < k; j++) {
                num = num * 2;  // Multiply the element by 2
            }

            // Calculate the maximum OR value considering the current element
            ans = max(ans, pre[i] | num | suf[i + 1]);
        }

        return ans;  // Return the maximum OR value
    }
};
