class Solution {
public:
    long long maximumOr(vector<int>& nums, int k) {
        vector<long long> prefix(nums.size()),suffix(nums.size());
        long long num=0;
        for(int i=0;i<nums.size();i++){
            num=nums[i]|num;
            prefix[i]=num;
        }
        num=0;
        for(int i=nums.size()-1;i>=0;i--){
            num=nums[i]|num;
            suffix[i]=num;
        }
        long long maxi=INT_MIN;
        for(int i=0;i<nums.size();i++){
                long long var=nums[i]*(long long)(pow(2,k));
                if(i!=0)var=var|prefix[i-1];
                if(i!=nums.size()-1)var=var|suffix[i+1];
                maxi=max(maxi,var);
        }
        return maxi;
    }
};
