class Solution:
    def maximumOr(self, nums: List[int], k: int) -> int:
        # use count as dict to store 32 bit numbers
        count = [0] * 32
        # count all bit numbers
        for n in nums:
            for i in range(32):
                if n >> i & 1:
                    count[i] += 1

        ans = 0
        for n in nums:
            # as or operation need to use temp to deduct n as bits
            temp = count.copy()
            for i in range(32):
                if n >> i & 1:
                    temp[i] -= 1

            res = 0
            for i in range(32):
                if temp[i]:
                    res += 1 << i
            # use greedy to brute force all cases
            res |= n << k
            ans = max(ans, res)

        return ans
