int64_t maximumOr(int* nums, int numsSize, int k){
	int prefixOr[numsSize];
	prefixOr[0] = nums[0];
	for (int i = 1; i < numsSize; i += 1){
		prefixOr[i] = prefixOr[i - 1] | nums[i];
	}

	int suffixOr[numsSize];
	suffixOr[numsSize - 1] = nums[numsSize - 1];
	for (int i = numsSize - 2; i >= 0; i -= 1){
		suffixOr[i] = suffixOr[i + 1] | nums[i];
	}

	int64_t max = 0;

	for (int i = 0; i < numsSize; i += 1){
		const int64_t cur = (
			( (i - 1 >= 0)? (prefixOr[i - 1]) : (0) ) | 
			( (int64_t)nums[i] << k ) | 
			( (i + 1 < numsSize)? (suffixOr[i + 1]) : (0) )
		);
		if (cur > max){
			max = cur;
		}
	}

	return max;
}
