# Code
```
class Solution {

    private lateinit var dpCache: Array<LongArray>
    private lateinit var mem: LongArray

    private fun doFind(nums: IntArray, index: Int, k: Int): Long {
        if(index >= nums.size) {
            return 0L
        }
        if(dpCache[index][k] != -1L) {
            return dpCache[index][k]
        }
        if(k == 0) {
            dpCache[index][k] = mem[index]
            return mem[index]
        }
        var r = 0L
        var m = 1L
        val ni = nums[index].toLong()
        for(i in 0 .. k) {
            val c = ni * m
            if(c < ni) {
                break
            }
            r = maxOf(r, (c or doFind(nums, index+1, k-i)))
            m *= 2L
        }
        dpCache[index][k] = r
        return r
    }

    fun maximumOr(nums: IntArray, k: Int): Long {
        dpCache = Array<LongArray>(nums.size+1) { LongArray(k+1) { -1 }}
        mem = LongArray(nums.size) { 0 }
        for(i in nums.size-1 downTo 0) {
            mem[i] = nums[i].toLong() or (if(i+1 <= nums.size-1) mem[i+1] else 0L)
        }
        return doFind(nums, 0, k)
    }
}
```

