- Only consider shift numbers with `hightest bit` set
- Implement `OR` by `bit counting`, to have O(1) time `OR`-ing all numbers with update on one number due to shift
```
class Solution {
public:
    long long maximumOr(vector<int>& nums, int k) {
        long long bitmask[48] = {};
        for(int i = 0; i < 48; ++i) 
            bitmask[i] = 1LL << i;

        vector<int> bitcount(48, 0);
        for(auto n : nums) {
            for(int i = 0; i < 32; ++i) 
                if(n & bitmask[i])
                    bitcount[i]++;
        }

        int highestbit = 1 << int(log2(*max_element(nums.begin(), nums.end())));
        long long ans = 0;
        for(int i = 0; i < nums.size(); ++i) {
            if(nums[i] & highestbit) {
                vector<int> bitcount2(bitcount);
                for(int j = 0; j < 32; ++j) {
                    if(nums[i] & bitmask[j]) {
                        bitcount2[j]--;
                        bitcount2[j+k]++;
                    }
                }
                long long cur = 0;
                for(int i = 0; i < 48; ++i)
                    if(bitcount2[i])
                        cur |= bitmask[i];

                ans = max(ans, cur);
            }
        }
        return ans;
    }
};
```

