        if(dp[index][k] != -1) return dp[index][k];
        long long ans = INT_MIN;
        for(int i = 0; i<=k; i++) ans = max(ans, (long long)nums[index]*(1<<i) | maxResult(nums, k-i, index+1, dp));

        return dp[index][k] = ans;

    }

    long long solveTab(vector<int>&nums, int k){
      int n = nums.size();
      vector<vector<long long>>dp(n+1, vector<long long>(k+1, 0));

      for(int i = n-1; i>-1; i--){
          for(int j = k; j>-1; j--){
            long long ans = INT_MIN;
            for(int l = 0; l<=j; l++)
              ans = max(ans, (long long)nums[i]*(1<<l) | dp[i+1][j-l]);
            dp[i][j] = ans;
          }
      }

      return dp[0][k];

