so for an element at index i we do (nums[i]*(1<<k)) and OR it with the left 0 --> i-1 element and right i+1 --> n-1 element

```
class Solution {
public:
    long long maximumOr(vector<int>& nums, int k) {
        long long s = 0, res = 0, t = 1<<k;
        int n = nums.size();
        vector<int> l = nums,r = nums;

        for(int i=1; i<n; ++i) l[i] |= l[i-1];
        for(int i=n-2; i>=0; --i) r[i] |= r[i+1];

        for(int i=0; i<nums.size(); ++i){
            int x = i+1 > n-1 ? 0 : r[i+1], y = i-1 < 0 ? 0 : l[i-1];
            res = max(res,(nums[i]*t | x | y));
        }

        return res;
    }
};
