class Solution {
public:
    long long maximumOr(vector<int>& nums, int k) {
        long long ans=0,n=nums.size();
        int a[n],b[n],i,x=0;
        for(i=n-1;i>=0;i--) {
            x|=nums[i]; a[i]=x; // SUFFIX
        }
        x=0;
        for(i=0;i<n;i++) {
            x|=nums[i]; b[i]=x; // PREFIX
        }
        for(i=0;i<n;i++) {
            long long res=0,p=nums[i],q=k;
            if(i>0) res|=b[i-1];
            while(q--) p*=2;
            res|=p;
            if(i<n-1) res|=a[i+1];
            ans=max(ans,res);
        }
        return ans;
    }
};
