class Solution {
public:
    long long maximumOr(vector<int>&v, int k) {
        int n = v.size(),c,maxi = -1,ct=0;
        long long ans=0;

        sort(v.begin(),v.end(),greater<int>());
        vector<long long> dp1(n,0),dp2(n,0),ds(n,0);
        dp1[0] = v[0];
        dp2[n-1] = v[n-1];
        for(int i=1;i<n;++i)
        {
            dp1[i] = dp1[i-1] | v[i];
        }
        for(int i=n-2;i>=0;i--)
        {
            dp2[i] = dp2[i+1] | v[i];
        }

        for(int i=0;i<n;++i)
        {
            int p = v[i];
            if(v[i]!=1)
                c = log2(p);
            if(p==1)
                c=0;
            if(c==maxi)
            {
                ct++;
            }
            if(c>maxi)
            {
                maxi  =c;
                ct=1;
            }
        }
        // cout<<maxi<<" "<<ct<<endl;
        if(ct==1)
        {
            while(k--)
                v[0] = 2*v[0];
            for(int i=0;i<n;++i)
                ans = ans|v[i];
        }
        else
        {
            long long res = -1;
            for(int i=0;i<ct;++i)
            {
                long long int cd =v[i];
                int dc = k;
                // cout<<dc<<endl;
                while(dc>0)
                {
                    cd = cd*2;
                    dc--;
                }

                // cout<<cd<<endl;
                long long tk = cd,left =0,right=0;

                if(i>0)
                    left = dp1[i-1];
                if(i<n-1)
                    right = dp2[i+1];
                tk = tk | left | right;
                res = max(res,tk);    
                // cout<<left<<" "<<right<<" "<<res<<endl;
            }
            ans  = res;

        }
        return ans;

    }
};
```

