    partial_sum(begin(nums), end(nums), back_inserter(pref_or), bit_or<>());
    long long res = 0, suf_or = 0;
    for (int i = nums.size() - 1; i >= 0; suf_or |= nums[i--])
        res = max(res, pref_or[i] | ((long long)nums[i] << k) | suf_or);
