class Solution {
public:
    long long solveMemo(vector<int> &arr,int ind,int k, vector<vector<long long>> &dp){
        if(ind>=arr.size())
            return 0;
        if(dp[ind][k]!=-1)
            return dp[ind][k];
        long long ans = 0;

        for(int i = 0; i<=k; i++){
            ans = max(ans,((1LL << i)*arr[ind])|(solveMemo(arr,ind+1,k-i, dp)));
        }

        return dp[ind][k] = ans;
    }

    long long maximumOr(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<long long>> dp(n+1, vector<long long>(k+1, -1));
        return solveMemo(nums, 0, k, dp);
    }
};
```

