class Solution {
public:
    long long maximumOr(vector<int>& nums, int k) {
        long long ans=0;
        if(nums.size()==1){
            ans=nums[0];
            while(k--){
                ans*=2;
            }
            return ans;
        }
        long long sum=0;
        for(auto it:nums){
            sum=(sum|it);
        }
        int dp[nums.size()][32];
        memset(dp,0,sizeof(dp));
        for(int i=0; i<32; i++){
            for(int j=0; j<nums.size(); j++){
                if((nums[j]&(1<<i))){
                    if(j!=0)
                    dp[j][i]=1+dp[j-1][i];
                    else dp[j][i]=1;
                }
                else{
                    if(j!=0)
                    dp[j][i]=dp[j-1][i];
                }
            }
        }
        string t="";
        while(sum>0){
            t+=char(sum%2+'0');
            sum/=2;
        }
        cout<<t<<" "; 
        int n=nums.size()-1;
        for(int i=0; i<nums.size(); i++){
            string s="";
            for(int j=0; j<32; j++){
                if(j>=t.length()) break;
                if(t[j]=='0'){
                    s+=t[j];
                }
                else{
                    if(!(nums[i]&(1<<j))) s+=t[j];
                    else{
                    if(dp[n][j]>1){
                        s+="1";
                    }
                    else s+="0";
                    }
                }
            }
            long long dks=1;
            cout<<s<<" ";
            long long ans1=0;
            for(int j=0; j<s.length(); j++){
                ans1+=dks*(s[j]-'0');
                dks*=2;
            }
            int k1=k;
            long long j=nums[i];
            while(k1--){
                j*=2;
            }
            ans1=(ans1|j);
            ans=max(ans,ans1);
        }
        return ans;
    }
};
```

