class Solution:
    def maximumOr(self, nums: List[int], k: int) -> int:
        array=[0]*32
        lists=[]
        for n in nums:
            lst=[]
            while n:
                lst.append(n&1)
                n>>=1
            for i, l in enumerate(lst):
                array[i]+=l
            lists.append(lst)
        ans=0
        for i, n in enumerate(nums):
            array2=array.copy()
            for j, l in enumerate(lists[i]):
                array2[j]-=l
            s=0
            p=1
            for a in array2:
                s|=p if a!=0 else 0
                p<<=1
            s|=n<<k
            if s>ans:
                ans=s
        return ans      
```

