using LL = long long ;
class Solution {
public:
    long long maximumOr(vector<int>& nums, int k) {
        int n = nums.size() ;
        sort(nums.begin(), nums.end()) ;
        reverse(nums.begin(), nums.end()) ;
        LL ans = 0 ;
        for(int i = 0; i < n; i++){
            LL ret = ((LL)nums[i] << k) ;
            for(int j = 0; j < n; j++){
                if(j == i)
                    continue ;
                ret |= nums[j] ;
            }
            if(ans > ret)
                break ;
            ans = max(ans, ret) ;
        }
        return ans ;
    }
};
```

