class Solution {
public:
    long long f(int i, int k, vector<int>& nums,vector<vector<long long>>&dp)
    {
        if(i==-1) return 0;
        if(dp[i][k]!=-1) return dp[i][k];
        long long ans = 0;
        for(int j=0;j<=k;j++)
            ans = max(ans,(1ll*nums[i]<<j) | 1ll*f(i-1,k-j,nums,dp));
        return dp[i][k] = ans;
    }
    long long maximumOr(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<long long>> dp(n,vector<long long>(k+1,-1));
       return f(n-1,k,nums,dp);

    }
};
