        int pfR[] = new int[n];
        for(int i=0,j=n-1; i<n ; i++,j--){
            if(i==0){
                pfL[i] = 0;
                pfR[j] = 0;
            }
            else{
                pfL[i] = pfL[i-1] | nums[i-1];
                pfR[j] = pfR[j+1] | nums[j+1];
            }
