                    res[i] = s[i];
                    res[j] = s[i];
                } else {
                    res[i] = s[j];
                    res[j] = s[j];
                }
            } else{
                res[i] = s[i];
                res[j] = s[i];
            }
            i++;
            j--;
        }
        return res;
    }
};
