class Solution {
public:
    string makeSmallestPalindrome(string s) {
        int n=s.length();
        for(int i=0; i<n/2; i++){
            if(s[i]!=s[n-1-i]){
                if(s[i]>s[n-i-1]){
                    s[i]=min(s[i], s[n-1-i]);
                }else s[n-1-i]=min(s[i],s[n-1-i]);
            }
        }return s; 
    }
};
