            for(int i = 0; i <= (len/2) - 1; i++){
                if(str1.charAt(i) != str1.charAt(len - i - 1)){
                    char c1 = str1.charAt(i);
                    char c2 = str1.charAt(len - i - 1);
                    char c = (c1 < c2)? c1 : c2;
                    str1.setCharAt(i, c);
