       int i=0;
       String str="";
       int j=s.length-1;
       while(i<j)
       {
         if(s[i]!=s[j])
         {
            if(s[i]<s[j])
            {
               s[j]=s[i];
            }
            else if(s[i]>s[j])
            {
            s[i]=s[j];
            }
         }
         i++;
         j--;
       }
       for(char item:s)
       str+=item;
       return str;
    }
}
