            int currExtra = (mp.count(currStr))? 0 : currStr.size();
            int nextExtra = solve(s, mp, dp, cutIdx + 1);
            int totalExtra = currExtra + nextExtra;

            minExtra = min(minExtra, totalExtra);
        }
        return dp[index] = minExtra;
